import React, { useContext } from 'react'; // Import React and useContext at the top
import { useParams } from 'react-router-dom'; // Import useParams from react-router-dom
import Breadcrum from '../Components/Breadcrums/Breadcrum'; // Import Breadcrum component
import { ShopContext } from '../Contexts/ShopContext'; // Import ShopContext from its defined location

const Product = () => {
    const { productId } = useParams(); // Destructure productId from useParams
    const { all_product } = useContext(ShopContext); // Access ShopContext
    const product = all_product.find((e) => e.id === Number(productId)); // Find the product based on ID

    return (
        <div>
            <Breadcrum />
            <h1>{product ? product.name : 'Product Not Found'}</h1>
            <p>{product ? product.description : 'No description available'}</p>
        </div>
    );
};

export default Product;
