import React, { useContext } from 'react';
import { useParams } from 'react-router-dom'; // Import useParams from react-router-dom
import Breadcrum from '../Components/Breadcrums/Breadcrum';
import { ShopContext } from '../Contexts/ShopContext'; // Import ShopContext from its defined location

const Product = () => {
  const { all_product } = useContext(ShopContext); // Use ShopContext
  const { productId } = useParams(); // Get productId from route parameters
  const product = all_product.find((e) => e.id === Number(productId)); // Match productId to find the product

  return (
    <div>
      <Breadcrum product={product} />
    </div>
  );
};

export default Product;
